    public static List<News> extractNews() {
        List<News> news = new ArrayList<>();

        try {
            JSONObject baseJsonObject = new JSONObject(NEWS_REQUEST_URL);
            JSONArray newsArray = baseJsonObject.getJSONArray("articles");
            for (int i = 0; i < newsArray.length(); i++) {

                JSONObject properties = newsArray.getJSONObject(i);

                String title = properties.getString("title");
                String desc = properties.getString("description");

                News news1 = new News(title, desc);
                news.add(news1);
            }

        } catch (JSONException e) {
            Log.e(LOG_TAG, "Problem with parsing news Json", e);
        }

        return news;
    }
